(function($, window, document, undefined) {
    var $window = $(window);

    $.fn.lazyload = function(options) {
        var elements = this;
        var $container;
        var settings = {
            threshold       : 0,
            failure_limit   : 0,
            event           : "scroll",
            effect          : "show",
            container       : window,
            data_attribute  : "original",
            skip_invisible  : true,
            appear          : null,
            load            : null
        };

        function update() {
            var counter = 0;
      
            elements.each(function() {
                var $this = $(this);
                if (settings.skip_invisible && !$this.is(":visible")) {
                    return;
                }
                if ($.abovethetop(this, settings) ||
                    $.leftofbegin(this, settings)) {
                        /* Nothing. */
                } else if (!$.belowthefold(this, settings) &&
                    !$.rightoffold(this, settings)) {
                        $this.trigger("appear");
                        /* if we found an image we'll load, reset the counter */
                        counter = 0;
                } else {
                    if (++counter > settings.failure_limit) {
                        return false;
                    }
                }
            });

        }

        if(options) {
            /* Maintain BC for a couple of versions. */
            if (undefined !== options.failurelimit) {
                options.failure_limit = options.failurelimit; 
                delete options.failurelimit;
            }
            if (undefined !== options.effectspeed) {
                options.effect_speed = options.effectspeed; 
                delete options.effectspeed;
            }

            $.extend(settings, options);
        }

        /* Cache container as jQuery as object. */
        $container = (settings.container === undefined ||
                      settings.container === window) ? $window : $(settings.container);

        /* Fire one scroll event per scroll. Not one scroll event per image. */
        if (0 === settings.event.indexOf("scroll")) {
            $container.bind(settings.event, function(event) {
                return update();
            });
        }

        this.each(function() {
            var self = this;
            var $self = $(self);

            self.loaded = false;

            /* When appear is triggered load original image. */
            $self.one("appear", function() {
                if (!this.loaded) {
                    if (settings.appear) {
                        var elements_left = elements.length;
                        settings.appear.call(self, elements_left, settings);
                    }
                    $("<img />")
                        .bind("load", function() {
                            $self
                                .hide()
                                .attr("src", $self.data(settings.data_attribute))
                                [settings.effect](settings.effect_speed);
                            self.loaded = true;

                            /* Remove image from array so it is not looped next time. */
                            var temp = $.grep(elements, function(element) {
                                return !element.loaded;
                            });
                            elements = $(temp);

                            if (settings.load) {
                                var elements_left = elements.length;
                                settings.load.call(self, elements_left, settings);
                            }
                        })
                        .attr("src", $self.data(settings.data_attribute));
                }
            });

            /* When wanted event is triggered load original image */
            /* by triggering appear.                              */
            if (0 !== settings.event.indexOf("scroll")) {
                $self.bind(settings.event, function(event) {
                    if (!self.loaded) {
                        $self.trigger("appear");
                    }
                });
            }
        });

        /* Check if something appears when window is resized. */
        $window.bind("resize", function(event) {
            update();
        });
              
        /* With IOS5 force loading images when navigating with back button. */
        /* Non optimal workaround. */
        if ((/iphone|ipod|ipad.*os 5/gi).test(navigator.appVersion)) {
            $window.bind("pageshow", function(event) {
                if (event.originalEvent.persisted) {
                    elements.each(function() {
                        $(this).trigger("appear");
                    });
                }
            });
        }

        /* Force initial check if images should appear. */
        $(window).load(function() {
            update();
        });
        
        return this;
    };

    /* Convenience methods in jQuery namespace.           */
    /* Use as  $.belowthefold(element, {threshold : 100, container : window}) */

    $.belowthefold = function(element, settings) {
        var fold;
        
        if (settings.container === undefined || settings.container === window) {
            fold = $window.height() + $window.scrollTop();
        } else {
            fold = $(settings.container).offset().top + $(settings.container).height();
        }

        return fold <= $(element).offset().top - settings.threshold;
    };
    
    $.rightoffold = function(element, settings) {
        var fold;

        if (settings.container === undefined || settings.container === window) {
            fold = $window.width() + $window.scrollLeft();
        } else {
            fold = $(settings.container).offset().left + $(settings.container).width();
        }

        return fold <= $(element).offset().left - settings.threshold;
    };
        
    $.abovethetop = function(element, settings) {
        var fold;
        
        if (settings.container === undefined || settings.container === window) {
            fold = $window.scrollTop();
        } else {
            fold = $(settings.container).offset().top;
        }

        return fold >= $(element).offset().top + settings.threshold  + $(element).height();
    };
    
    $.leftofbegin = function(element, settings) {
        var fold;
        
        if (settings.container === undefined || settings.container === window) {
            fold = $window.scrollLeft();
        } else {
            fold = $(settings.container).offset().left;
        }

        return fold >= $(element).offset().left + settings.threshold + $(element).width();
    };

    $.inviewport = function(element, settings) {
         return !$.rightoffold(element, settings) && !$.leftofbegin(element, settings) &&
                !$.belowthefold(element, settings) && !$.abovethetop(element, settings);
     };

    /* Custom selectors for your convenience.   */
    /* Use as $("img:below-the-fold").something() or */
    /* $("img").filter(":below-the-fold").something() which is faster */

    $.extend($.expr[':'], {
        "below-the-fold" : function(a) { return $.belowthefold(a, {threshold : 0}); },
        "above-the-top"  : function(a) { return !$.belowthefold(a, {threshold : 0}); },
        "right-of-screen": function(a) { return $.rightoffold(a, {threshold : 0}); },
        "left-of-screen" : function(a) { return !$.rightoffold(a, {threshold : 0}); },
        "in-viewport"    : function(a) { return $.inviewport(a, {threshold : 0}); },
        /* Maintain BC for couple of versions. */
        "above-the-fold" : function(a) { return !$.belowthefold(a, {threshold : 0}); },
        "right-of-fold"  : function(a) { return $.rightoffold(a, {threshold : 0}); },
        "left-of-fold"   : function(a) { return !$.rightoffold(a, {threshold : 0}); }
    });

})(jQuery, window, document);

$(document).ready(function() {

    //ios
    $('#avatar').draggable();

    //
    $('#avatar').jqDrag().mousedown(function(){$('#tip').fadeOut(100).remove();})

    $('#avatar ul').path({
        radius: 90,
        radian: 360,
        duration: 160
    });
    
    $("#portfolio_inset img").lazyload({
        threshold : 30
    });

    $.expandedHeaderTransition = (function($, p) {

        var config = {
                header_behaviour_threshold: 750,
                logoAnimationThreshold: 260,
                taglineAnimationThreshold: 260
            },
            status = {
                logo: 0, // large logo
                tagline: 1, // visible
            }
            cache = {
                $expandedHeader: $('#ExpandedHeader'),
                $siteTagline: $('#intro'),
                $expandedHeaderTitle: $('#ExpandedHeader h3'),
                $compactHeaderTitle: $('#tagline'),
                $compactHeaderTitleWrap : $('#CompactHeader'),
            }

        p.init = function() {

            $(window).scroll( function() { scrollMonitor() } );
            
        }

        var scrollMonitor = function() {
            var scrollPos = $(window).scrollTop();

            if($(window).width() >= config.header_behaviour_threshold){
                
                if( scrollPos > config.taglineAnimationThreshold && status.tagline ) {
                    // fade it out
                    cache.$expandedHeader.stop().animate( { opacity: 0 }, { duration: 350, easing: 'easeInQuint' } );
                    // cache.$siteTagline.stop().animate( { opacity: 0 }, { duration: 350, easing: 'easeInQuint' } );

                    status.tagline = 0;
                } else if( scrollPos < config.taglineAnimationThreshold && !status.tagline ) {
                    // fade it in
                    cache.$expandedHeader.stop().animate( { opacity: 1 }, { duration: 350, easing: 'easeInQuint' } );

                    status.tagline = 1;
                }


                if( scrollPos > config.logoAnimationThreshold && !status.logo ) {
                    // expanded logo is up, bring compact in
                    cache.$expandedHeaderTitle.stop().animate( { opacity: 0, top: 200 }, { duration: 500, easing: 'easeInQuint' } );

                    cache.$compactHeaderTitle.stop().css('top',-100).animate( { opacity: 1, top: 0 }, { duration: 500, easing: 'easeOutSine' } );

                    status.logo = 1;
                } else if( scrollPos < config.logoAnimationThreshold && status.logo ) {
                    // compact logo is up, bring expanded in
                    cache.$compactHeaderTitle.stop().animate( { opacity: 0 }, { duration: 300, easing: 'easeInQuint', complete: function(){

                        cache.$compactHeaderTitle.stop().css({ top: -300, opacity: 0 });
                        cache.$expandedHeaderTitle.stop().css({ top: 0 }).animate( { opacity: 1 }, { duration: 500, easing: 'easeInQuint' } );

                    } } );

                    status.logo = 0;
                }
                
                if(scrollPos > 476 ){
                    cache.$compactHeaderTitleWrap.addClass('floating');
                    $('#portfolio_inset').css({
                        'margin-top' : 40
                    })
                    // $('#avatar-w').fadeOut()
                } else {
                    cache.$compactHeaderTitleWrap.removeClass('floating');
                    $('#portfolio_inset').css({
                        'margin-top' : 0
                    })
                    // $('#avatar-w').fadeIn()
                }

            }
        }

        p.resetForMobile = function() {
            cache.$expandedHeader.attr('style','');

            cache.$compactHeaderTitle
                .css('top', -320 )
                .css( 'opacity', 0 );

            cache.$expandedHeaderTitle
                .attr('style', '');

        }

        return p;
    }(jQuery, $.expandedHeaderTransition || {}));

    $.expandedHeaderTransition.init();
    if( $(window).width() <= 600) {
        $.expandedHeaderTransition.resetForMobile();
    }

    if( $(window).width() <= 750 ) {
        $.expandedHeaderTransition.resetForMobile()
    }

    $('#tagline a').click(function(){
        $(document).stop().scrollTo(0, 400);
    })
})

